using System.Numerics;

namespace Recursion
{
	internal static class Numbers
	{
		internal static readonly BigInteger NumberThatIsSmall =
			new BigInteger(12345);
		internal static readonly BigInteger NumberThatIsLargeButSafe =
			BigInteger.Parse("7893174893174893174931871");
		// This number will cause a StackOverflowException no matter what:
		internal static readonly BigInteger NumberThatWillAlwaysCauseStackOverflow =
			BigInteger.Parse("");
	}
}
